//Jacob Lorenzo//MaineShell Program: simulates a small scale shell#include <stdio.h>#include <string.h>#include <stdlib.h>int main(){    //sets up arrays for parsing, parsestore stores the parsed result and input is input    char parseStore1[10],parseStore2[10],parseStore3[20],parseStore4[20],input[20];    //while loop keeps going until the exit function is used    while(1){    printf("COS135@UMaine>>");    fgets(input,40,stdin);    //sets the memory to prevent any garbled output    memset(parseStore1, '\0', sizeof(parseStore1));    memset(parseStore2, '\0', sizeof(parseStore2));    memset(parseStore3, '\0', sizeof(parseStore3));    memset(parseStore4, '\0', sizeof(parseStore4));        strncpy(parseStore1,input,4);    //input validation    if (strlen(parseStore1) < 4){        main();    }    //checks for exit in input    if (strcmp(parseStore1,"exit") == 0){        exit(1);    }    //checks to see if help was inputted into the command    if(strcmp(parseStore1,"help") == 0){        printf("show path (show path to current directory)\n");        printf("create <<filename or directory name>> (create a new file or directory)\n");        printf("show files (list only the files in the current directory)\n");        printf("show directories (list only the folders in the current directory)\n");        printf("exit (quit the Maine Shell and return to the terminal prompt)\n");        main();    }    //if neither help or exit were listed, it parses the rest of the input    strncpy(parseStore2,input,6);        strncpy(parseStore3,input+5,strlen(input)-5);    strncpy(parseStore4,input+6,strlen(input)-6);    //creates file using system to touch to make the file or mkdir for folder    if(strstr(parseStore2, "create") != NULL){       	char newFile[256];	if (strstr(parseStore4, ".") != NULL) {    		sprintf(newFile,"touch %s",parseStore4);        	system(newFile);	}else{		sprintf(newFile,"mkdir %s",parseStore4);        system(newFile);	}	    }    //checks for show keyword then checks for what word follows    if(strstr(parseStore1, "show") != NULL){        if(strstr(parseStore3, "files") != NULL){            system("ls -p | egrep -v /$");        }else if(strstr(parseStore3, "directories") != NULL){	        system("ls -d */");        }else if(strstr(parseStore3, "path") != NULL){            system("pwd");        }    }    }}